package ejercisios.clase.wrappers;

public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

//		byte primitivo
		byte datoByte = 2; // primitivo
		Byte datoByteWrapper = 3;// Wrapper
		System.out.println(" primitivo:" + datoByte);
		System.out.println(" Wrapper:" + datoByteWrapper);
		// Conversiones
		byte datoByte1 = 6;
		Byte datoConvertido = datoByte1;
		System.out.println(" Byte convertido:" + datoConvertido);

//		short
		short datoShort = 1;// primitivo
		Short datoShortWrapper = 1;// Wrapper
		System.out.println(" primitivo:" + datoShort);
		System.out.println(" Wrapper:" + datoShortWrapper);
		// Conversiones
		short datoShort1 = 4;// primitivo
		Short shortConvertido = datoShort1;// Wrapper
		System.out.println(" Short convertido:" + shortConvertido);

//		int
		int edad = 45;// primitivo
		Integer edadWrapper = 48;// Wrapper
		System.out.println(" primitivo:" + edad);
		System.out.println(" Wrapper:" + edadWrapper);
//		Conversiones
		int edad1 = 45;// primitivo
		Integer edadConvertido = edad1;// Wrapper
		System.out.println(" Int convertido:" + edadConvertido);

//		long
		long valorGrande = 12444;// primitivo
		Long valorGrandeWrapper = 123121L;// Wrapper
		System.out.println(" primitivo:" + valorGrande);
		System.out.println(" Wrapper:" + valorGrandeWrapper);
//		Conversiones
		long valorGrande1 = 12444;// primitivo
		Long valorGrandeConvertido = valorGrande1;// Wrapper
		System.out.println(" Long convertido:" + valorGrandeConvertido);

//		boolean
		boolean valorBoolean = true;// primitivo
		Boolean valorBooleanWrapper = false;// Wrapper
		System.out.println(" primitivo:" + valorBoolean);
		System.out.println(" Wrapper:" + valorBooleanWrapper);
//		Conversiones
		boolean valorBoolean1 = true;// primitivo
		Boolean valorBooleanConvertido = valorBoolean1;// Wrapper
		System.out.println(" Boolean convertido:" + valorBooleanConvertido);

//		float
		float valorFloat = 12345678;// primitivo
		Float valorFloatWrapper =33455;// Wrapper
		System.out.println(" primitivo:" + valorFloat);
		System.out.println(" Wrapper:" + valorFloatWrapper);
//		Conversiones
		float valorFloat1 = 12345678;// primitivo
		Float valorFloatConvertido = valorFloat1;// Wrapper
		System.out.println(" Float convertido:" + valorFloatConvertido);

//		double
		double valorDouble = 13.2323f;// primitivo
		Double valorDoubleWrapper = 2.467;// Wrapper
		System.out.println(" primitivo:" + valorDouble);
		System.out.println(" Wrapper:" + valorDoubleWrapper);
//		Conversiones
		double valorDouble1 = 13.2323f;// primitivo
		Double valorDoubleConvertido = valorDouble1;// Wrapper
		System.out.println(" Double convertido:" + valorDoubleConvertido);

//		char
		char valorChar = 345;// primitivo
		Character valorCharWrapper = 234;// Wrapper
		System.out.println(" primitivo:" + valorChar);
		System.out.println(" Wrapper:" + valorCharWrapper);
//		Conversiones
		char valorChar1 = 343;// primitivo
		Character valorCharconvertido = valorChar1;// Wrapper
		System.out.println(" Char convertido:" + valorCharconvertido);

	}

}
